DROP PROCEDURE IF EXISTS `Login`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `Login`(un varchar(255))
BEGIN
	#Routine body goes here...
	select UserId,UserPassWord FROM he_user WHERE UserName = un;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for Register
-- ----------------------------
DROP PROCEDURE IF EXISTS `Register`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `Register`(un varchar(255),up varchar(255))
BEGIN
	#Routine body goes here...
	declare num int;
	SELECT Count(UserId) INTO num FROM he_user WHERE UserName = un;
	IF num = 1 THEN
		SELECT 0 as mode;
	ELSE
		SET @uid = uuid();
		SELECT UserId, 1 as mode FROM he_user WHERE UserId = @uid;		
	END IF;	
END
;;
DELIMITER ;

